import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertUserSchema, 
  insertVaskSchema, 
  insertLikeSchema, 
  insertCommentSchema, 
  insertUserSettingsSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth routes
  app.post("/api/auth/wallet", async (req, res) => {
    try {
      const { walletAddress, ensName } = req.body;

      if (!walletAddress) {
        return res.status(400).json({ message: "Wallet address is required" });
      }

      let user = await storage.getUserByWalletAddress(walletAddress);

      if (!user) {
        const userData = insertUserSchema.parse({
          walletAddress,
          ensName: ensName || null,
          displayName: ensName || `${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}`,
          bio: null,
          profileImage: null,
          coverImage: null
        });
        user = await storage.createUser(userData);
      } else if (ensName && user.ensName !== ensName) {
        user = await storage.updateUser(user.id, { ensName });
      }

      res.json({ user });
    } catch (error) {
      console.error("Auth error:", error);
      res.status(500).json({ message: "Authentication failed" });
    }
  });

  // User routes
  app.get("/api/users/:id", async (req, res) => {
    try {
      const user = await storage.getUserProfile(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Failed to get user" });
    }
  });

  app.patch("/api/users/:id", async (req, res) => {
    try {
      const updates = insertUserSchema.partial().parse(req.body);
      const user = await storage.updateUser(req.params.id, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Update user error:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Vask routes
  app.get("/api/vasks", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const offset = parseInt(req.query.offset as string) || 0;
      const vasks = await storage.getVasks(limit, offset);
      res.json(vasks);
    } catch (error) {
      console.error("Get vasks error:", error);
      res.status(500).json({ message: "Failed to get vasks" });
    }
  });

  app.get("/api/vasks/:id", async (req, res) => {
    try {
      const vask = await storage.getVask(req.params.id);
      if (!vask) {
        return res.status(404).json({ message: "Vask not found" });
      }
      res.json(vask);
    } catch (error) {
      console.error("Get vask error:", error);
      res.status(500).json({ message: "Failed to get vask" });
    }
  });

  app.post("/api/vasks", async (req, res) => {
    try {
      const vaskData = insertVaskSchema.parse(req.body);
      const vask = await storage.createVask(vaskData);
      res.status(201).json(vask);
    } catch (error) {
      console.error("Create vask error:", error);
      res.status(500).json({ message: "Failed to create vask" });
    }
  });

  app.delete("/api/vasks/:id", async (req, res) => {
    try {
      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      const success = await storage.deleteVask(req.params.id, userId);
      if (!success) {
        return res.status(404).json({ message: "Vask not found or unauthorized" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete vask error:", error);
      res.status(500).json({ message: "Failed to delete vask" });
    }
  });

  app.get("/api/users/:id/vasks", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const offset = parseInt(req.query.offset as string) || 0;
      const vasks = await storage.getVasksByUser(req.params.id, limit, offset);
      res.json(vasks);
    } catch (error) {
      console.error("Get user vasks error:", error);
      res.status(500).json({ message: "Failed to get user vasks" });
    }
  });

  app.post("/api/vasks/:id/pin", async (req, res) => {
    try {
      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      const success = await storage.pinVask(req.params.id, userId);
      if (!success) {
        return res.status(404).json({ message: "Vask not found or unauthorized" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Pin vask error:", error);
      res.status(500).json({ message: "Failed to pin vask" });
    }
  });

  // Like routes
  app.post("/api/vasks/:id/like", async (req, res) => {
    try {
      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      const existingLike = await storage.getLike(req.params.id, userId);
      if (existingLike) {
        return res.status(400).json({ message: "Already liked" });
      }

      const likeData = insertLikeSchema.parse({
        vaskId: req.params.id,
        userId
      });
      const like = await storage.createLike(likeData);
      res.status(201).json(like);
    } catch (error) {
      console.error("Create like error:", error);
      res.status(500).json({ message: "Failed to create like" });
    }
  });

  app.delete("/api/vasks/:id/like", async (req, res) => {
    try {
      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      const success = await storage.deleteLike(req.params.id, userId);
      if (!success) {
        return res.status(404).json({ message: "Like not found" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete like error:", error);
      res.status(500).json({ message: "Failed to delete like" });
    }
  });

  app.get("/api/users/:id/likes", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const offset = parseInt(req.query.offset as string) || 0;
      const vasks = await storage.getLikedVasks(req.params.id, limit, offset);
      res.json(vasks);
    } catch (error) {
      console.error("Get liked vasks error:", error);
      res.status(500).json({ message: "Failed to get liked vasks" });
    }
  });

  // Comment routes
  app.get("/api/vasks/:id/comments", async (req, res) => {
    try {
      const comments = await storage.getComments(req.params.id);
      res.json(comments);
    } catch (error) {
      console.error("Get comments error:", error);
      res.status(500).json({ message: "Failed to get comments" });
    }
  });

  app.post("/api/vasks/:id/comments", async (req, res) => {
    try {
      const commentData = insertCommentSchema.parse({
        ...req.body,
        vaskId: req.params.id
      });
      const comment = await storage.createComment(commentData);
      res.status(201).json(comment);
    } catch (error) {
      console.error("Create comment error:", error);
      res.status(500).json({ message: "Failed to create comment" });
    }
  });

  app.delete("/api/comments/:id", async (req, res) => {
    try {
      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      const success = await storage.deleteComment(req.params.id, userId);
      if (!success) {
        return res.status(404).json({ message: "Comment not found or unauthorized" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete comment error:", error);
      res.status(500).json({ message: "Failed to delete comment" });
    }
  });

  // Settings routes
  app.get("/api/users/:id/settings", async (req, res) => {
    try {
      const settings = await storage.getUserSettings(req.params.id);
      res.json(settings || { theme: "dark", preferences: {} });
    } catch (error) {
      console.error("Get settings error:", error);
      res.status(500).json({ message: "Failed to get settings" });
    }
  });

  app.patch("/api/users/:id/settings", async (req, res) => {
    try {
      const settingsData = insertUserSettingsSchema.parse({
        ...req.body,
        userId: req.params.id
      });
      const settings = await storage.updateUserSettings(req.params.id, settingsData);
      res.json(settings);
    } catch (error) {
      console.error("Update settings error:", error);
      res.status(500).json({ message: "Failed to update settings" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Vask, 
  type InsertVask, 
  type Like, 
  type InsertLike, 
  type Comment, 
  type InsertComment, 
  type UserSettings, 
  type InsertUserSettings,
  type VaskWithAuthor,
  type CommentWithAuthor,
  type UserProfile
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByWalletAddress(walletAddress: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined>;
  getUserProfile(id: string): Promise<UserProfile | undefined>;

  // Vask methods
  getVask(id: string): Promise<VaskWithAuthor | undefined>;
  getVasks(limit?: number, offset?: number): Promise<VaskWithAuthor[]>;
  getVasksByUser(userId: string, limit?: number, offset?: number): Promise<VaskWithAuthor[]>;
  createVask(vask: InsertVask): Promise<Vask>;
  deleteVask(id: string, userId: string): Promise<boolean>;
  pinVask(id: string, userId: string): Promise<boolean>;
  unpinVask(userId: string): Promise<boolean>;

  // Like methods
  getLike(vaskId: string, userId: string): Promise<Like | undefined>;
  createLike(like: InsertLike): Promise<Like>;
  deleteLike(vaskId: string, userId: string): Promise<boolean>;
  getLikedVasks(userId: string, limit?: number, offset?: number): Promise<VaskWithAuthor[]>;

  // Comment methods
  getComments(vaskId: string): Promise<CommentWithAuthor[]>;
  createComment(comment: InsertComment): Promise<Comment>;
  deleteComment(id: string, userId: string): Promise<boolean>;

  // Settings methods
  getUserSettings(userId: string): Promise<UserSettings | undefined>;
  updateUserSettings(userId: string, settings: InsertUserSettings): Promise<UserSettings>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private vasks: Map<string, Vask>;
  private likes: Map<string, Like>;
  private comments: Map<string, Comment>;
  private userSettings: Map<string, UserSettings>;

  constructor() {
    this.users = new Map();
    this.vasks = new Map();
    this.likes = new Map();
    this.comments = new Map();
    this.userSettings = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByWalletAddress(walletAddress: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.walletAddress.toLowerCase() === walletAddress.toLowerCase(),
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id, 
      createdAt: new Date(),
      displayName: insertUser.displayName || null,
      ensName: insertUser.ensName || null,
      bio: insertUser.bio || null,
      profileImage: insertUser.profileImage || null,
      coverImage: insertUser.coverImage || null
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getUserProfile(id: string): Promise<UserProfile | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const vaskCount = Array.from(this.vasks.values()).filter(v => v.authorId === id).length;
    const likeCount = Array.from(this.likes.values()).filter(l => l.userId === id).length;
    const commentCount = Array.from(this.comments.values()).filter(c => c.authorId === id).length;

    return {
      ...user,
      vaskCount,
      likeCount,
      commentCount
    };
  }

  private async buildVaskWithAuthor(vask: Vask, currentUserId?: string): Promise<VaskWithAuthor> {
    const author = this.users.get(vask.authorId)!;
    const likeCount = Array.from(this.likes.values()).filter(l => l.vaskId === vask.id).length;
    const commentCount = Array.from(this.comments.values()).filter(c => c.vaskId === vask.id).length;
    const isLiked = currentUserId ? Array.from(this.likes.values()).some(l => l.vaskId === vask.id && l.userId === currentUserId) : false;

    return {
      ...vask,
      author,
      likeCount,
      commentCount,
      isLiked
    };
  }

  async getVask(id: string): Promise<VaskWithAuthor | undefined> {
    const vask = this.vasks.get(id);
    if (!vask) return undefined;
    return this.buildVaskWithAuthor(vask);
  }

  async getVasks(limit = 50, offset = 0): Promise<VaskWithAuthor[]> {
    const allVasks = Array.from(this.vasks.values())
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(offset, offset + limit);

    return Promise.all(allVasks.map(vask => this.buildVaskWithAuthor(vask)));
  }

  async getVasksByUser(userId: string, limit = 50, offset = 0): Promise<VaskWithAuthor[]> {
    const userVasks = Array.from(this.vasks.values())
      .filter(v => v.authorId === userId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(offset, offset + limit);

    return Promise.all(userVasks.map(vask => this.buildVaskWithAuthor(vask, userId)));
  }

  async createVask(insertVask: InsertVask): Promise<Vask> {
    const id = randomUUID();
    const vask: Vask = { 
      ...insertVask, 
      id, 
      createdAt: new Date(),
      isPinned: false,
      ipfsHash: insertVask.ipfsHash || null
    };
    this.vasks.set(id, vask);
    return vask;
  }

  async deleteVask(id: string, userId: string): Promise<boolean> {
    const vask = this.vasks.get(id);
    if (!vask || vask.authorId !== userId) return false;

    this.vasks.delete(id);
    // Delete associated likes and comments
    Array.from(this.likes.entries()).forEach(([key, like]) => {
      if (like.vaskId === id) this.likes.delete(key);
    });
    Array.from(this.comments.entries()).forEach(([key, comment]) => {
      if (comment.vaskId === id) this.comments.delete(key);
    });

    return true;
  }

  async pinVask(id: string, userId: string): Promise<boolean> {
    const vask = this.vasks.get(id);
    if (!vask || vask.authorId !== userId) return false;

    // Unpin any existing pinned vask
    await this.unpinVask(userId);

    // Pin this vask
    vask.isPinned = true;
    this.vasks.set(id, vask);
    return true;
  }

  async unpinVask(userId: string): Promise<boolean> {
    const userVasks = Array.from(this.vasks.values()).filter(v => v.authorId === userId && v.isPinned);
    userVasks.forEach(vask => {
      vask.isPinned = false;
      this.vasks.set(vask.id, vask);
    });
    return true;
  }

  async getLike(vaskId: string, userId: string): Promise<Like | undefined> {
    return Array.from(this.likes.values()).find(
      like => like.vaskId === vaskId && like.userId === userId
    );
  }

  async createLike(insertLike: InsertLike): Promise<Like> {
    const id = randomUUID();
    const like: Like = { 
      ...insertLike, 
      id, 
      createdAt: new Date()
    };
    this.likes.set(id, like);
    return like;
  }

  async deleteLike(vaskId: string, userId: string): Promise<boolean> {
    const like = Array.from(this.likes.entries()).find(
      ([_, like]) => like.vaskId === vaskId && like.userId === userId
    );
    if (!like) return false;

    this.likes.delete(like[0]);
    return true;
  }

  async getLikedVasks(userId: string, limit = 50, offset = 0): Promise<VaskWithAuthor[]> {
    const userLikes = Array.from(this.likes.values())
      .filter(l => l.userId === userId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(offset, offset + limit);

    const likedVasks = userLikes
      .map(like => this.vasks.get(like.vaskId))
      .filter(Boolean) as Vask[];

    return Promise.all(likedVasks.map(vask => this.buildVaskWithAuthor(vask, userId)));
  }

  async getComments(vaskId: string): Promise<CommentWithAuthor[]> {
    const vaskComments = Array.from(this.comments.values())
      .filter(c => c.vaskId === vaskId)
      .sort((a, b) => a.createdAt.getTime() - b.createdAt.getTime());

    return vaskComments.map(comment => ({
      ...comment,
      author: this.users.get(comment.authorId)!
    }));
  }

  async createComment(insertComment: InsertComment): Promise<Comment> {
    const id = randomUUID();
    const comment: Comment = { 
      ...insertComment, 
      id, 
      createdAt: new Date()
    };
    this.comments.set(id, comment);
    return comment;
  }

  async deleteComment(id: string, userId: string): Promise<boolean> {
    const comment = this.comments.get(id);
    if (!comment || comment.authorId !== userId) return false;

    this.comments.delete(id);
    return true;
  }

  async getUserSettings(userId: string): Promise<UserSettings | undefined> {
    return Array.from(this.userSettings.values()).find(s => s.userId === userId);
  }

  async updateUserSettings(userId: string, settings: InsertUserSettings): Promise<UserSettings> {
    const existing = await this.getUserSettings(userId);

    if (existing) {
      const updated = { ...existing, ...settings };
      this.userSettings.set(existing.id, updated);
      return updated;
    }

    const id = randomUUID();
    const newSettings: UserSettings = { 
      ...settings, 
      id, 
      userId,
      theme: settings.theme || null,
      preferences: settings.preferences || null
    };
    this.userSettings.set(id, newSettings);
    return newSettings;
  }
}

export const storage = new MemStorage();

