import { ethers, BrowserProvider } from 'ethers';

export interface WalletConnection {
  address: string;
  ensName?: string;
  provider: BrowserProvider;
}

declare global {
  interface Window {
    ethereum?: any;
  }
}

export class Web3Service {
  private provider: BrowserProvider | null = null;
  private signer: ethers.Signer | null = null;

  async connectMetaMask(): Promise<WalletConnection> {
    // For demo/testing purposes, provide a mock wallet if MetaMask is not available
    if (!window.ethereum) {
      console.log('MetaMask not available, using demo wallet');
      const mockAddress = '0x742d35Cc6634C0532925a3b8D457fCAF8B9a6C07';
      return {
        address: mockAddress,
        ensName: 'demo.eth',
        provider: null as any // Mock provider for demo
      };
    }

    try {
      await window.ethereum.request({ method: 'eth_requestAccounts' });

      this.provider = new ethers.BrowserProvider(window.ethereum);
      this.signer = await this.provider.getSigner();

      const address = await this.signer.getAddress();
      let ensName: string | undefined;

      try {
        ensName = await this.provider.lookupAddress(address) || undefined;
      } catch (error) {
        console.warn('ENS lookup failed:', error);
      }

      return {
        address,
        ensName,
        provider: this.provider
      };
    } catch (error) {
      console.error('MetaMask connection failed:', error);
      throw new Error('Failed to connect to MetaMask');
    }
  }

  async connectWalletConnect(): Promise<WalletConnection> {
    // For now, we'll implement a simple fallback
    // In a real implementation, you'd use WalletConnect SDK
    throw new Error('WalletConnect integration coming soon');
  }

  async signMessage(message: string): Promise<string> {
    if (!this.signer) {
      throw new Error('No wallet connected');
    }
    return this.signer.signMessage(message);
  }

  async getNetwork() {
    if (!this.provider) {
      throw new Error('No provider available');
    }
    return this.provider.getNetwork();
  }

  isConnected(): boolean {
    return !!this.provider && !!this.signer;
  }

  disconnect() {
    this.provider = null;
    this.signer = null;
  }

  getProvider() {
    return this.provider;
  }

  getSigner() {
    return this.signer;
  }
}

export const web3Service = new Web3Service();

