                <p className="text-sm text-muted-foreground">{profile.vaskCount} Vasks</p>
              </div>
            </div>
          </header>

          {/* Profile Header */}
          <div className="bg-card border-b border-border">
            <div className="relative">
              {/* Cover Image */}
              <div className="h-48 bg-gradient-to-br from-primary to-secondary relative">
                {isOwnProfile && (
                  <Button
                    size="icon"
                    variant="secondary"
                    className="absolute bottom-4 right-4"
                    data-testid="button-edit-cover"
                  >
                    <Camera className="h-4 w-4" />
                  </Button>
                )}
              </div>

              {/* Profile Info */}
              <div className="relative px-4 pb-4">
                <div className="flex flex-col sm:flex-row sm:items-end sm:space-x-4">
                  <div className="relative -mt-16 mb-4 sm:mb-0">
                    <div className="w-32 h-32 bg-accent rounded-full border-4 border-background flex items-center justify-center">
                      <span className="text-4xl font-bold text-white">
                        {(profile.displayName || profile.ensName || profile.walletAddress)[0].toUpperCase()}
                      </span>
                    </div>
                    {isOwnProfile && (
                      <Button
                        size="icon"
                        className="absolute bottom-2 right-2 w-8 h-8 bg-primary rounded-full"
                        data-testid="button-edit-avatar"
                      >
                        <Camera className="text-sm" />
                      </Button>
                    )}
                  </div>

                  <div className="flex-1 space-y-2">
                    <div className="flex items-start justify-between">
                      <div>
                        <h1 className="text-2xl font-bold" data-testid="text-display-name">
                          {profile.displayName || profile.ensName || "Anonymous"}
                        </h1>
                        {profile.ensName && (
                          <p className="text-muted-foreground" data-testid="text-ens-name">
                            {profile.ensName}
                          </p>
                        )}
                        <p className="text-sm text-muted-foreground font-mono" data-testid="text-wallet-address">
                          {profile.walletAddress}
                        </p>
                      </div>
                      {isOwnProfile && (
                        <Button
                          className="wallet-button"
                          onClick={() => setLocation("/settings")}
                          data-testid="button-edit-profile"
                        >
                          Edit Profile
                        </Button>
                      )}
                    </div>

                    {profile.bio && (
                      <p className="text-foreground leading-relaxed" data-testid="text-bio">
                        {profile.bio}
                      </p>
                    )}

                    <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                      <span>
                        <Calendar className="inline mr-1 h-4 w-4" />
                        Joined {new Date(profile.createdAt).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
                      </span>
                    </div>

                    <div className="flex items-center space-x-6 text-sm">
                      <span>
                        <strong data-testid="text-vask-count">{profile.vaskCount}</strong>{" "}
                        <span className="text-muted-foreground">Vasks</span>
                      </span>
                      <span>
                        <strong data-testid="text-like-count">{profile.likeCount}</strong>{" "}
                        <span className="text-muted-foreground">Likes</span>
                      </span>
                      <span>
                        <strong data-testid="text-comment-count">{profile.commentCount}</strong>{" "}
                        <span className="text-muted-foreground">Comments</span>
