          {/* Profile Tabs */}
          <div className="border-b border-border">
            <nav className="flex">
              <button className="px-6 py-4 border-b-2 border-primary text-primary font-medium">
                Vasks
              </button>
              <button className="px-6 py-4 text-muted-foreground hover:text-foreground transition-colors">
                Likes
              </button>
              <button className="px-6 py-4 text-muted-foreground hover:text-foreground transition-colors">
                Comments
              </button>
            </nav>
          </div>

          {/* Profile Content */}
          <div className="divide-y divide-border">
            {vasksLoading ? (
              <div className="space-y-4 p-4">
                {Array.from({ length: 3 }).map((_, i) => (
                  <div key={i} className="space-y-3">
                    <div className="flex space-x-3">
                      <Skeleton className="w-12 h-12 rounded-full" />
                      <div className="flex-1 space-y-2">
                        <Skeleton className="h-4 w-1/4" />
                        <Skeleton className="h-4 w-full" />
                        <Skeleton className="h-4 w-3/4" />
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : userVasks && userVasks.length > 0 ? (
              userVasks.map((vask) => (
                <VaskCard key={vask.id} vask={vask} currentUser={user} />
              ))
            ) : (
              <div className="p-8 text-center text-muted-foreground">
                <p>
                  {isOwnProfile 
                    ? "You haven't posted any vasks yet." 
                    : "This user hasn't posted any vasks yet."
                  }
                </p>
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Mobile Bottom Navigation */}
      {isMobile && <MobileNav />}
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useWallet } from "@/hooks/use-wallet";
import { useTheme } from "@/hooks/use-theme";
import { useIndexedDB } from "@/hooks/use-indexeddb";
import { useIsMobile } from "@/hooks/use-mobile";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

import Sidebar from "@/components/layout/sidebar";
import MobileNav from "@/components/layout/mobile-nav";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { ArrowLeft, Download, Trash2, Wallet as WalletIcon } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function SettingsPage() {
  const [, setLocation] = useLocation();
  const { isConnected, user, disconnect } = useWallet();
  const { theme, toggleTheme } = useTheme();
  const { exportData, clear } = useIndexedDB();
  const isMobile = useIsMobile();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [displayName, setDisplayName] = useState("");
  const [bio, setBio] = useState("");

  useEffect(() => {
    if (!isConnected) {
      setLocation("/");
    }
  }, [isConnected, setLocation]);

  useEffect(() => {
    if (user) {
      setDisplayName(user.displayName || "");
      setBio(user.bio || "");
    }
  }, [user]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: { displayName?: string; bio?: string }) => {
      const response = await apiRequest('PATCH', `/api/users/${user?.id}`, data);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(['/api/users', user?.id], data);
      toast({
        title: "Profile Updated",
        description: "Your profile has been successfully updated.",
      });
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Failed to update your profile. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleSaveProfile = () => {
    updateProfileMutation.mutate({
      displayName: displayName.trim() || undefined,
      bio: bio.trim() || undefined,
    });
  };

  const handleExportData = async () => {
    try {
      const data = await exportData();
      const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `vasukii-data-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast({
        title: "Data Exported",
        description: "Your data has been downloaded successfully.",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export your data.",
        variant: "destructive",
      });
    }
  };

  const handleDeleteLocalData = async () => {
    if (window.confirm("Are you sure you want to delete all local data? This action cannot be undone.")) {
      try {
        await clear();
        toast({
          title: "Data Deleted",
          description: "All local data has been removed from this device.",
        });
      } catch (error) {
        toast({
          title: "Deletion Failed",
          description: "Failed to delete local data.",
          variant: "destructive",
        });
      }
    }
  };

  const handleDisconnectWallet = () => {
    if (window.confirm("Are you sure you want to disconnect your wallet?")) {
      disconnect();
      setLocation("/");
    }
  };

  if (!isConnected || !user) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-6xl mx-auto flex">
        {/* Left Sidebar - Desktop */}
        {!isMobile && <Sidebar />}

        {/* Main Content */}
        <main className="flex-1">
          {/* Header */}
          <header className="sticky top-0 bg-background/95 backdrop-blur border-b border-border p-4 z-10">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setLocation("/home")}
                data-testid="button-back"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <div>
                <h2 className="text-2xl font-bold" data-testid="page-title">Settings</h2>
                <p className="text-muted-foreground">Manage your Vasukii experience</p>
              </div>
            </div>
          </header>

          <div className="max-w-2xl mx-auto p-4 space-y-6">
            {/* Appearance */}
            <Card className="bg-card border">
              <CardHeader>
                <CardTitle className="text-lg font-semibold">Appearance</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="font-medium">Dark Mode</p>
                    <p className="text-sm text-muted-foreground">
                      Switch between light and dark themes
                    </p>
                  </div>
                  <Switch
                    checked={theme === "dark"}
                    onCheckedChange={toggleTheme}
                    data-testid="switch-theme"
                  />
                </div>
              </CardContent>
            </Card>

            {/* Profile */}
            <Card className="bg-card border">
              <CardHeader>
                <CardTitle className="text-lg font-semibold">Profile</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="displayName">Display Name</Label>
                  <Input
                    id="displayName"
                    type="text"
                    placeholder="Your display name"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                    data-testid="input-display-name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="bio">Bio</Label>
                  <Textarea
                    id="bio"
                    placeholder="Tell us about yourself"
                    value={bio}
                    onChange={(e) => setBio(e.target.value)}
                    rows={3}
                    data-testid="textarea-bio"
                  />
                </div>
                <Button
                  onClick={handleSaveProfile}
                  disabled={updateProfileMutation.isPending}
                  className="wallet-button"
                  data-testid="button-save-profile"
                >
                  {updateProfileMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </CardContent>
            </Card>

            {/* Data Rights */}
            <Card className="bg-card border">
              <CardHeader>
                <CardTitle className="text-lg font-semibold">Data Rights</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="p-4 bg-muted rounded-lg">
                  <h3 className="font-medium mb-2">How Your Data is Stored</h3>
                  <ul className="text-sm text-muted-foreground space-y-1">
                    <li>â€¢ Profile settings stored locally in your browser</li>
                    <li>â€¢ Vasks stored on IPFS for decentralized access</li>
                    <li>â€¢ Interactions recorded on blockchain</li>
                    <li>â€¢ You maintain full control of your data</li>
                  </ul>
                </div>

                <div className="space-y-3">
                  <Button
                    onClick={handleExportData}
                    variant="outline"
                    className="w-full p-3 text-left justify-start"
                    data-testid="button-export-data"
                  >
                    <div className="flex items-center justify-between w-full">
                      <div>
                        <p className="font-medium">Export Your Data</p>
                        <p className="text-sm text-muted-foreground">
                          Download all your local data
                        </p>
                      </div>
                      <Download className="h-4 w-4" />
                    </div>
                  </Button>

                  <Button
                    onClick={handleDeleteLocalData}
                    variant="outline"
                    className="w-full p-3 text-left justify-start border-destructive text-destructive hover:bg-destructive/10"
                    data-testid="button-delete-data"
                  >
                    <div className="flex items-center justify-between w-full">
                      <div>
                        <p className="font-medium">Delete Local Data</p>
                        <p className="text-sm text-muted-foreground">
                          Remove all data from this device
                        </p>
                      </div>
                      <Trash2 className="h-4 w-4" />
                    </div>
                  </Button>
                </div>
              </CardContent>
            </Card>

            {/* Wallet */}
            <Card className="bg-card border">
              <CardHeader>
                <CardTitle className="text-lg font-semibold">Wallet Connection</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between p-4 border border-border rounded-lg">
                  <div className="flex items-center space-x-3">
                    <WalletIcon className="text-xl text-primary" />
                    <div>
                      <p className="font-medium">MetaMask</p>
                      <p className="text-sm text-muted-foreground font-mono" data-testid="text-wallet-address">
                        {user.walletAddress}
                      </p>
                    </div>
                  </div>
                  <Button
                    onClick={handleDisconnectWallet}
                    variant="outline"
                    data-testid="button-disconnect-wallet"
                  >
                    Disconnect
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        </main>
      </div>

      {/* Mobile Bottom Navigation */}
      {isMobile && <MobileNav />}
    </div>
  );
}

client/src/components/layout/mobile-nav..content:
import { useLocation } from "wouter";
import { useWallet } from "@/hooks/use-wallet";
import { Home, User, Settings } from "lucide-react";
import { cn } from "@/lib/utils";

export default function MobileNav() {
  const [location, setLocation] = useLocation();
  const { user } = useWallet();

  const navItems = [
    {
      name: "Home",
      icon: Home,
      path: "/home",
      testId: "mobile-nav-home"
    },
    {
      name: "Profile",
      icon: User,
      path: `/profile/${user?.id}`,
      testId: "mobile-nav-profile"
    },
    {
      name: "Settings",
      icon: Settings,
      path: "/settings",
      testId: "mobile-nav-settings"
    },
  ];

  if (!user) return null;

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-card border-t border-border p-2 md:hidden">
      <div className="flex justify-around">
        {navItems.map((item) => {
          const Icon = item.icon;
          const isActive = location === item.path || 
            (item.path === "/home" && location === "/") ||
            (item.path.startsWith("/profile") && location.startsWith("/profile"));

          return (
            <button
              key={item.name}
              onClick={() => setLocation(item.path)}
              className={cn(
                "nav-item flex flex-col items-center p-2 rounded-lg transition-colors",
                isActive 
                  ? "active bg-primary text-primary-foreground" 
                  : "hover:bg-accent"
              )}
              data-testid={item.testId}
            >
              <Icon className="text-lg" />
              <span className="text-xs mt-1">{item.name}</span>
            </button>
          );
        })}
      </div>
    </nav>
  );
}

