import { useState } from "react";
import { useWallet } from "@/hooks/use-wallet";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { ipfsService } from "@/lib/ipfs";

import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { Image, BarChart3 } from "lucide-react";

export default function ComposeVask() {
  const [content, setContent] = useState("");
  const { user } = useWallet();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createVaskMutation = useMutation({
    mutationFn: async (vaskData: { content: string; authorId: string; ipfsHash?: string }) => {
      const response = await apiRequest('POST', '/api/vasks', vaskData);
      return response.json();
    },
    onSuccess: () => {
      setContent("");
      queryClient.invalidateQueries({ queryKey: ['/api/vasks'] });
      if (user) {
        queryClient.invalidateQueries({ queryKey: ['/api/users', user.id, 'vasks'] });
      }
      toast({
        title: "Vask Posted!",
        description: "Your vask has been published to the decentralized web.",
      });
    },
    onError: () => {
      toast({
        title: "Post Failed",
        description: "Failed to post your vask. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handlePost = async () => {
    if (!content.trim() || !user) return;

    try {
      // Upload to IPFS
      const ipfsResponse = await ipfsService.uploadText(content);

      await createVaskMutation.mutateAsync({
        content: content.trim(),
        authorId: user.id,
        ipfsHash: ipfsResponse.hash
      });
    } catch (error) {
      console.error("Failed to create vask:", error);
      toast({
        title: "Post Failed",
        description: "Failed to upload to IPFS. Please try again.",
        variant: "destructive",
      });
    }
  };

  const characterCount = content.length;
  const maxLength = 280;
  const isDisabled = !content.trim() || characterCount > maxLength || createVaskMutation.isPending;

  if (!user) return null;

  return (
    <div className="space-y-4">
      <div className="flex space-x-3">
        <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
          <span className="text-white font-medium">
            {(user.displayName || user.ensName || user.walletAddress)[0].toUpperCase()}
          </span>
        </div>
        <div className="flex-1">
          <Textarea
            placeholder="What's happening in Web3?"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className="w-full bg-transparent text-xl placeholder-muted-foreground resize-none border-none outline-none ring-0 focus-visible:ring-0 min-h-[120px]"
            maxLength={maxLength}
            data-testid="textarea-compose"
          />
        </div>
      </div>

      <div className="flex items-center justify-between pl-15">
        <div className="flex items-center space-x-4 text-sm text-muted-foreground">
          <Button
            variant="ghost"
            size="icon"
            className="hover:text-primary transition-colors"
            disabled
            data-testid="button-add-image"
          >
            <Image className="h-5 w-5" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            className="hover:text-primary transition-colors"
            disabled
            data-testid="button-add-poll"
          >
            <BarChart3 className="h-5 w-5" />
          </Button>
          <span 
            className={`text-xs ${characterCount > maxLength ? 'text-destructive' : ''}`}
            data-testid="text-character-count"
          >
            {characterCount}/{maxLength}
          </span>
        </div>
        <Button
          onClick={handlePost}
          disabled={isDisabled}
          className="wallet-button py-2 px-6 rounded-full text-white font-medium disabled:opacity-50"
          data-testid="button-post-vask"
        >
          {createVaskMutation.isPending ? "Posting..." : "Post Vask"}
        </Button>
      </div>
    </div>
  );
}

